        if self.capclaseipec_set.filter(status=True).exists():
            mensaje += "horarios "
        if self.exiten_inscritos():
            mensaje += "inscritos "
        if not mensaje == "":
            s = mensaje.split(" ")
            mensaje = ""
            if len(s) == 4:
                mensaje = s[0] + ", " + s[1] + ", " + s[2]
            elif len(s) == 3:
                mensaje = s[0] + ", " + s[1]
            else:
                mensaje = s[0]
        else:
            mensaje = None
        return mensaje

