    def flexbox_repr(self):
        return self.denominacionpuesto.descripcion + " - " + self.escalaocupacional.descripcion + " - " + self.get_tipogrado_display() + " - " + str(
            self.rmu)

    def flexbox_alias(self):
        return self.denominacionpuesto.descripcion, self.escalaocupacional.descripcion + "," + self.get_tipogrado_display() + "," + str(
            self.rmu)


