    def resultecnico(self):
        return (self.resulminutos() * (float(self.ptecnico)) / 100)

    def resuldireccionper(self):
        valorcalculo = self.cabecera.periodo.valorcalculo if self.cabecera.periodo.valorcalculo else 9225.84096
